var twoSum = function (nums, target) {
  let left = 0;
  let right = 0;

  let currentSum = 0;

  for (let num of nums) {
    currentSum += num;

    while (currentSum > target) {
      currentSum -= nums[left];
      left += 1;
    }
    if (currentSum === target) {
      return [left, right];
    }
    right += 1;
  }
  return [];
};

