var kClosest = function (points, k) {
  const unordered = points.map(([x, y], index) => {
    return [index, Math.sqrt(x * x + y * y)]; // as x2, y2 is origin (0,0)
  });
  const ordered = unordered.sort((a, b) => a[1] - b[1]);
  return ordered.slice(0, k).map(([index]) => points[index]);
};

