function findCharLengthRequired(arr, maxLength) {
  let total = 0;
  for (let data of arr) {
    let [_, count] = data;
    total += maxLength - count;
  }
  return total;
}

function findMaxLength(arrs) {
  let maxLength = 0;
  for (let arr of arrs) {
    let [_, count] = arr;
    if (count > maxLength) {
      maxLength = count;
    }
  }
  return maxLength;
}
