function containerWithMostWater(arr) {
  let maxWater = 0;
  l = 0;
  r = arr.length - 1;
  while (l < r) {
    maxWater = Math.max(maxWater, Math.min(arr[l], arr[r]) * (r - l));
    if (arr[l] < arr[r]) {
      l++;
    } else {
      r--;
    }
  }

