function integerToRoman(num) {
  let romanList = {
    M: 1000,
    CM: 900,
    D: 500,
    CD: 400,
    C: 100,
    XC: 90,
    L: 50,
    XL: 40,
    X: 10,
    IX: 9,
    V: 5,
    IV: 4,
    I: 1,
  };

  let result = "";

  for (let item in romanList) {
    let numberOfTimes = num / romanList[item];
    if (numberOfTimes !== 0) {
      result += item.repeat(numberOfTimes);
    }
    num %= romanList[item];
  }

  return result;
}

