function longestSubStr(s) {
  let left = 0;
  let map = {};

  return s.split("").reduce((max, val, ind) => {
    left = map[val] >= left ? map[val] + 1 : left;

    map[val] = ind;

    return Math.max(max, ind - left + 1);
  }, 0);
}

