function productOfArrayExceptSelf(nums) {
  let zeroCount = 0;
  let prod = nums.reduce((p, v, i) => {
    if (v === 0) zeroCount += 1;
    return p * v;
  }, 1);
  console.log("asdas: ", prod);
  let noZeroProd = 0;
  if (prod === 0 && zeroCount === 1) {
    noZeroProd = nums.reduce((p, v, i) => {
      if (v != 0) {
        return p * v;
      }
      return p;
    }, 1);
  }

  nums = nums.map((e) => {
    if (e === 0) {
      return noZeroProd;
    }
    return prod / e;
  });
  return nums;
}

