var setZeroes = function (matrix) {
  var track = [];

  // find zeros
  for (var i = 0; i < matrix.length; i++) {
    for (var j = 0; j < matrix[0].length; j++) {
      if (matrix[i][j] === 0) track.push([i, j]);
    }
  }

  for (var i = 0; i < track.length; i++) {
    var [x, y] = track[i];

    // update row
    for (var j = 0; j < matrix[0].length; j++) {
      matrix[x][j] = 0;
    }

    // udpate column
    for (var j = 0; j < matrix.length; j++) {
      matrix[j][y] = 0;
    }
  }
};

var calculate = function (s) {
  let stack = [];
  let num = "";
  let sign = null;
  // we loop till the full length of the array to account for last sign
  for (let i = 0; i <= s.length; i++) {
    const curr = s[i];
    //handle space
    if (curr === " ") continue;
    //if char is a number
    if (!isNaN(curr)) num += curr;

    console.log("1:: ", num);
    //if we have a  sign + - / *
    if (isNaN(curr)) {
      // /\D/.test()
      num = Number(num);
      console.log("curr: ", curr);
      switch (sign) {
        case "+":
        case null:
          //we push the initial number into the stack
          stack.push(num);
          break;
        case "-":
          //we push any values after the subtraction sign as negative
          stack.push(-num);
          break;
        case "*":
          //we pop the stack then multiply and push back
          stack.push(stack.pop() * num);
          break;
        case "/":
          //we pop the stack then devide and push back
          stack.push(parseInt(stack.pop() / num, 10));
          break;
      }

      console.log(stack);
      // sign becomes current sign
      sign = curr;
      // we reset num
      num = "";
    }
  }
  //we reduce the array adding positive and negative numbers
  return stack.reduce((a, b) => {
    return a + b;
  }, 0);
};

