var levelOrder = function (root) {
  if (!root) return [];

  const queue = [root];
  const retVal = [];

  while (queue.length > 0) {
    const levelSize = queue.length;
    let levelValues = [];
    for (let i = 0; i < levelSize; i++) {
      const current = queue.shift();
      levelValues.push(current.val);
      if (current.left) queue.push(current.left);
      if (current.right) queue.push(current.right);
    }

    retVal.push(levelValues);
    levelValues = [];
  }

  return retVal;
};

var maxPathSum = function (root) {
  let max = Number.NEGATIVE_INFINITY;

  function dfs(node) {
    if (!node) {
      return 0;
    }

    let left = Math.max(dfs(node.left), 0);
    let right = Math.max(dfs(node.right), 0);

    max = Math.max(node.val + left + right, max);

    return node.val + Math.max(left, right);
  }

  dfs(root);

  return max;
};

var buildTree = function (preorder, inorder) {
  const indexMap = inorder.reduce((map, val, index) => {
    map[val] = index;
    return map;
  }, {});

  let currentRootIndex = 0;

  const constructTree = (left, right) => {
    if (left > right) return null;

    const currentRootVal = preorder[currentRootIndex++];
    const index = indexMap[currentRootVal];
    const node = new TreeNode(currentRootVal);

    node.left = constructTree(left, index - 1);
    node.right = constructTree(index + 1, right);

    return node;
  };

  return constructTree(0, preorder.length - 1);
};

function processData(input) {
  input.sort(function (a, b) {
    return a - b;
  });

  var max = input[1] - input[0];
  var maxPairs = [input[0], input[1]];

  for (var i = 2; i < input.length; i++) {
    var prev = input[i - 1];
    var next = input[i];
    var diff = next - prev;

    if (diff < max) {
      max = diff;
      maxPairs = [prev, next];
    } else if (diff === max) {
      maxPairs = maxPairs.concat([prev, next]);
    }
  }

  console.log(maxPairs.join(" "));
}
