var connect = function (root) {
  if (root == null) return root;
  let queue = [root];
  while (queue.length != 0) {
    let next = [];
    while (queue.length != 0) {
      let node = queue.shift();
      node.next = queue[0] || null;
      if (node.left != null) {
        next.push(node.left);
      }
      if (node.right != null) {
        next.push(node.right);
      }
    }
    queue = next;
  }
  return root;
};

var isSymmetric = function (root) {
  const queue = [];

  queue.push(root, root);

  while (queue.length > 0) {
    const node1 = queue.shift();
    const node2 = queue.shift();

    if (node1 == null && node2 == null) continue;
    if (node1 == null || node2 == null) return false;
    if (node1.val != node2.val) return false;

    queue.push(node1.left, node2.right);
    queue.push(node1.right, node2.left);
  }

  return true;
};

var ladderLength = function (beginWord, endWord, wordList) {
  const wordSet = new Set(wordList);
  let queue = [beginWord];
  let steps = 1;

  while (queue.length) {
    const next = [];

    // loop over each word in the queue
    for (let word of queue) {
      if (word === endWord) return steps;

      // loop over each char of the word
      for (let i = 0; i < word.length; i++) {
        // and replace the char with letters from [a - z]
        for (let j = 0; j < 26; j++) {
          const newWord =
            word.slice(0, i) + String.fromCharCode(j + 97) + word.slice(i + 1);

          // if the new word exist in the word list add it to the queue
          if (wordSet.has(newWord)) {
            next.push(newWord);
            wordSet.delete(newWord);
          }
        }
      }
    }
    queue = next;
    steps++;
  }
  return 0;
};

ladderLength("hit", "cog", ["hot", "dot", "dog", "lot", "log", "cog"]);

var findLadders = function (beginWord, endWord, wordList) {
  if (!wordList.includes(endWord)) return [];

  const result = [],
    map = new Map(),
    steps = new Map();

  for (const word of wordList) {
    for (let i = 0; i < word.length; i++) {
      const hash = word.substring(0, i) + "*" + word.substring(i + 1);

      if (!map.has(hash)) map.set(hash, []);

      map.get(hash).push(word);
      steps.set(hash, wordList.length);
    }
  }

  const queue = [[beginWord]];

  while (queue.length > 0) {
    const current = queue.shift();
    const set = new Set(current); // for O(1) lookup time
    const lastWord = current[current.length - 1];
    if (lastWord === endWord) {
      if (result.length === 0 || current.length === result[0].length)
        result.push(current);
      else break; // early stop because the current path takes longer
    } else {
      for (let i = 0; i < lastWord.length; i++) {
        const hash = lastWord.substring(0, i) + "*" + lastWord.substring(i + 1);
        if (!map.has(hash) || current.length > steps.get(hash)) continue;

        for (const nextWord of map.get(hash))
          if (!set.has(nextWord))
            // don't want to go back to a word already in the path
            queue.push([...current, nextWord]);

        steps.set(hash, current.length);
      }
    }
  }
  return result;
};

var zigzagLevelOrder = function (root) {
  if (!root) return [];
  let queue = [root];
  let output = [];
  let deep = 0;
  while (queue.length > 0) {
    const size = queue.length;
    const level = [];

    for (let i = 0; i < size; i++) {
      const node = queue.shift();
      if (deep % 2 == 0) level.push(node.val);
      else level.unshift(node.val);

      if (node.left) queue.push(node.left);
      if (node.right) queue.push(node.right);
    }
    output.push(level);
    deep++;
  }

  return output;
};

