var addTwoNumbers = function (l1, l2) {
  var List = new ListNode(0);
  var head = List;
  var sum = 0;
  var carry = 0;

  while (l1 !== null || l2 !== null || sum > 0) {
    if (l1 !== null) {
      sum = sum + l1.val;
      l1 = l1.next;
    }
    if (l2 !== null) {
      sum = sum + l2.val;
      l2 = l2.next;
    }
    if (sum >= 10) {
      carry = 1;
      sum = sum - 10;
    }

    head.next = new ListNode(sum);
    head = head.next;

    sum = carry;
    carry = 0;
  }

  return List.next;
};

var addTwoNumbers = function (l1, l2) {
  let l1Len = getLen(l1);
  let l2Len = getLen(l2);
  let offset = Math.abs(l1Len - l2Len);
  if (l2Len > l1Len) [l1, l2] = [l2, l1]; // l1 will always be the longer one
  let dummyHead = new ListNode(0);
  dummyHead.val = getCarry(l1, l2, dummyHead, offset);

  return dummyHead.val == 1 ? dummyHead : dummyHead.next;
};

/**
 * @param {ListNode} l1
 * @param {ListNode} l2
 * @param {ListNode} node
 * @param {Number} offset
 */
function getCarry(l1, l2, node, offset) {
  if (l1 == null || l2 == null) return 0;
  node.next = new ListNode(0);
  let sum = 0;
  if (offset > 0) sum = l1.val + getCarry(l1.next, l2, node.next, offset - 1);
  else
    sum = l1.val + l2.val + getCarry(l1.next, l2.next, node.next, offset - 1);

  node.next.val = sum % 10;
  return parseInt(sum / 10); // return the carry
}

/**
 * @param {ListNode} root
 */
function getLen(root) {
  let len = 0;
  while (root) {
    len++;
    root = root.next;
  }
  return len;
}

var copyRandomList = function (head) {
  if (!head) {
    return null;
  }
  const clones = new Map();

  let n = head;
  while (n) {
    clones.set(n, new Node(n.val));
    n = n.next;
  }
  n = head;
  while (n) {
    clones.get(n).next = clones.get(n.next) || null;
    clones.get(n).random = clones.get(n.random) || null;
    n = n.next;
  }
  return clones.get(head);
};

var getIntersectionNode = function (headA, headB) {
  let a = headA,
    b = headB;
  while (a !== b) {
    a = !a ? headB : a.next;
    b = !b ? headA : b.next;
  }
  return a;
};

var mergeKLists = function (lists) {
  if (!lists.length) return null;

  function mergeTwoLists(a, b) {
    const dummyHead = new ListNode();
    let curA = a,
      curB = b,
      curD = dummyHead;

    while (curA && curB) {
      if (curA.val < curB.val) {
        curD.next = curA;
        curA = curA.next;
      } else {
        curD.next = curB;
        curB = curB.next;
      }
      curD = curD.next;
    }
    if (curA) curD.next = curA;
    if (curB) curD.next = curB;
    return dummyHead.next;
  }

  while (lists.length > 1) {
    const a = lists.shift();
    const b = lists.shift();
    const res = mergeTwoLists(a, b);
    lists.push(res);
  }
  return lists[0];
};

var mergeKLists_compare_one_by_one = function (lists) {
  if (!lists || !lists.length) return null;
  const findMinNode = (lists = lists) => {
    let index = -1,
      min = Number.MAX_SAFE_INTEGER;
    for (let i = 0; i < lists.length; i++) {
      if (!lists[i]) continue;
      if (lists[i].val <= min) {
        min = lists[i].val;
        index = i;
      }
    }

    let resNode = null;
    if (index !== -1) {
      resNode = lists[index];
      lists[index] = lists[index].next;
    }
    return resNode;
  };

  let res = new ListNode(-1),
    cur = res,
    temp = findMinNode(lists);
  while (temp) {
    cur.next = temp;
    cur = cur.next;
    temp = findMinNode(lists);
  }
  return res.next;
};

var mergeTwoLists = function (l1, l2) {
  var mergedHead = { val: -1, next: null },
    crt = mergedHead;
  while (l1 && l2) {
    if (l1.val > l2.val) {
      crt.next = l2;
      l2 = l2.next;
    } else {
      crt.next = l1;
      l1 = l1.next;
    }
    crt = crt.next;
  }
  crt.next = l1 || l2;

  return mergedHead.next;
};

function reverseLinkedList(node) {
  let newNode = {};
  let prevNode = null;
  while (node !== null) {
    if (Object.keys(newNode).length === 0) {
      newNode.value = node.value;
      newNode.next = null;
    }
    newNode = {
      value: node.value,
      next: newNode,
    };
    node = node.next;
  }

  return newNode;
}

// Time Complexity: O(n), Linear - traverse linked list only once
// Space Complexity: O(1), Constant - we will only have 2 pointers regardless of size of input; prev and temp

var reverseList = function (head) {
  // End of the reversed linked list set to null
  let prev = null;

  // Traverse through the given linked list
  while (head) {
    const temp = head.next; // References the next Node of given linked list
    head.next = prev; // head.next point to previous Node, instead of the next Node of the given linked list
    prev = head; // Move the prev Node pointer up to head
    head = temp; // Move the head up to next Node of the given linked list
  }

  // Prev is the reversed linked list
  return prev;
};

function reverseKGroup(head, k) {
  if (!head) return null;
  var tail = head;
  for (var i = 1; i < k; i++) {
    tail = tail.next;
    if (!tail) return head;
  }
  var next = tail.next;
  tail.next = null;
  reverse(head);
  head.next = reverseKGroup(next, k);
  return tail;
}

function reverse(curr) {
  var prev = null;
  while (curr) {
    var next = curr.next;
    curr.next = prev;
    prev = curr;
    curr = next;
  }
  return prev;
}

