function binary_search(arr, searchItem) {
  let left = 0;
  let right = arr.length - 1;

  while (left <= right) {
    const midpoint = Math.floor((left + right) / 2);
    if (arr[midpoint] === searchItem) {
      return midpoint;
    } else if (arr[midpoint] < searchItem) {
      left = midpoint + 1;
    } else {
      right = midpoint - 1;
    }
  }
  return null;
}

