const arr_num = [21, 10, 90, 65, 12, 55, 72, 11, 8, 101, 0, 21, 1];
function merge_sort(arr) {
  if (arr.length <= 1) {
    return arr;
  }
  const pivot = Math.floor(arr.length / 2);

  const left_arr = arr.slice(0, pivot);
  const right_arr = arr.slice(pivot);

  const left_arr_sorted = merge_sort(left_arr);
  const right_arr_sorted = merge_sort(right_arr);

  let i = 0;
  let j = 0;

  const sorted_arr = [];
  while (i < left_arr_sorted.length && j < right_arr_sorted.length) {
    if (left_arr_sorted[i] < right_arr_sorted[j]) {
      sorted_arr.push(left_arr_sorted[i]);
      i += 1;
    } else {
      sorted_arr.push(right_arr_sorted[j]);
      j += 1;
    }
  }
  sorted_arr.push(...left_arr_sorted.slice(i));
  sorted_arr.push(...right_arr_sorted.slice(j));

  return sorted_arr;
}

const sorted_arr_result = merge_sort(arr_num);
console.log("merge_sort item: ", sorted_arr_result);

const arr_num = [21, 10, 90, 65, 12, 55, 72, 11, 8];
function quick_sort(arr) {
  if (arr.length <= 1) {
    return arr;
  }
  const pivot = arr.splice(0, 1);
  const left_arr = [];
  const right_arr = [];

  for (let i = 0; i <= arr.length - 1; i++) {
    if (pivot > arr[i]) {
      left_arr.push(arr[i]);
    } else {
      right_arr.push(arr[i]);
    }
  }

  const left_sorted = quick_sort(left_arr);
  const right_sorted = quick_sort(right_arr);

  return left_sorted.concat(pivot).concat(right_sorted);
}

const sorted_arr_result = quick_sort(arr_num);
console.log("quick_sort item: ", sorted_arr_result);

const arr_num = [10, 90, 65, 12, 55, 72, 11, 8];
const sorted_arr = [];

function selection_sort(arr) {
  let min_num = arr[0];
  let min_num_index = 0;
  for (let i = 1; i <= arr.length - 1; i++) {
    if (arr[i] < min_num) {
      min_num_index = i;
      min_num = arr[i];
    }
  }

  sorted_arr.push(min_num);
  arr.splice(min_num_index, 1);
  if (arr.length === 1) {
    sorted_arr.push(arr[0]);
  } else {
    selection_sort(arr);
  }

  return sorted_arr;
}

