function useMemo(func) {
  let map = new Map();

  return (...a) => {
    let key = [...a].join("_");
    if (map.has(key)) {
      console.log("memo");
      return map.get(key);
    } else {
      let calFun = func(...a);
      map.set(key, calFun);
      return calFun;
    }
  };
}

