function myEmpty(obj) {
  return typeof obj === "object" && Object.keys(obj).length === 0;
}

let filterDeep = (obj, filterFun) => {
  let res = {};
  for (let key in obj) {
    if (typeof obj[key] === "object") {
      let innerObj = filterDeep(obj[key], filterFun);

      if (!myEmpty(innerObj)) {
        res[key] = innerObj;
      }
    } else if (filterFun(obj[key])) {
      res[key] = obj[key];
    }
  }
  return res;
};

