var simplifyPath = function (path) {
  let stack = [];
  path = path.split("/");

  for (let i = 0; i < path.length; i++) {
    if (path[i] == "." || path[i] == "") continue;
    if (path[i] == "..") stack.pop();
    else stack.push(path[i]);
  }

  return "/" + stack.join("/");
};

let throttleTimer;
const throttle = (callback, time) => {
  if (throttleTimer) return;
  throttleTimer = true;
  setTimeout(() => {
    callback();
    throttleTimer = false;
  }, time);
};

